	private final Pattern PHONE_REGEX = Pattern.compile("\\(?\\d{3}\\)?[- ]\\d{3}-\\d{4}");

	public boolean emailIsValid(String email) {
		Matcher m = EMAIL_REGEX.matcher(email);
		return m.matches();
	}

	public boolean phoneNumberIsValid(String phoneNumber) {
		Matcher m = PHONE_REGEX.matcher(phoneNumber);
		return m.matches();
	}

	public boolean stringIsValid(String name) {
		if (name == null || name == "")
			return false;
		return true;
	}

	public boolean userTypeIsValid(String userType) {
		if (userType.equals(SELLER) || userType.equals(BUYER) || userType.equals(ADMIN)) {
			return true;
		}
		return false;
	}
